import Reac,{Component} from 'react';


class App extends Component  {
  constructor(props) {
    super(props);
    this.state = {
      newItem : "",
      list:[]
    }
  }
  addItem(){
    const newItem = {
      id :  Math.random(),
      value : this.state.newItem
    };
    const list = [...this.state.list];
    list.push(newItem);

    this.setState({
      list,
      newItem:""
    });
  }
  render(){
    return(
      <div className="App">
        <div>
          Add an item .   .    .
          <br/>
          <input
             type="input"
             placeholder="add item . . . "
             value = {this.state.newItem}
             onChange = {e => this.updateInput("newItem",e.target.value)}
          />
        <button
          onClick = {()=>this.addItem()}
        >
        ADD
       </button>
        </div>
      </div>
    );
  }
}

export default App;
